vec3 grad(in float[2] i)
{
    vec3 col1=vec3(16.,16.,176.)/256.;vec3 col2=vec3(256.,256.,256.)/256.;vec3 col3=vec3(128.,96.,0.)/256.;
    return(col1*clamp(i[0]-i[1],0.,i[0])+col2*clamp(i[0]-abs(i[1]-i[0]),0.,i[0])+col3*clamp(i[1]-i[0],0.,i[0]))/i[0];
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord*2.-iResolution.xy)/iResolution.yy*pow(2.,texelFetch(iChannel1,ivec2(2,0),0).x)+texelFetch(iChannel1,ivec2(3,0),0).xy;
    vec3 col = vec3(0.,0.,0.);
    vec2 n = vec2(0.,0.);
    float pres = 100.;
    for(float i=0.;i<pres;i+=1.)
    {
        n = vec2(n.x*n.x-n.y*n.y,2.*n.x*n.y)+uv;
        if(length(n)>2.)
        {
            col=grad(float[2](pres,i));
            break;
        }
    }
    fragColor = vec4(col,1.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    if(ivec2(fragCoord.xy)==ivec2(1,0))
    {
    	fragColor=vec4(iMouse.x/iChannelResolution[0].x,0.,0.,0.);
    }
    if(ivec2(fragCoord.xy)==ivec2(2,0))
    {
        vec4 val=texelFetch(iChannel0,ivec2(2,0),0);
        vec4 oldMouse=texelFetch(iChannel0,ivec2(1,0),0);
        if(iMouse.z>0.5&&val.w>=0.5)
        {
            float deltaM=(iMouse.x/iChannelResolution[0].x)-oldMouse.x;
            val.x+=deltaM;
        }
        val.xy=val.xy;
        val.w=iMouse.z;
        fragColor=val;
    }
    if(ivec2(fragCoord.xy)==ivec2(3,0))
    {
        float z=pow(2.,texelFetch(iChannel0,ivec2(2,0),0).x)/100.;
        vec4 off=texelFetch(iChannel0,ivec2(3,0),0);
        vec2 left=texelFetch(iChannel1,ivec2(37,0),0).x*vec2(-z,0);
        vec2 up=texelFetch(iChannel1,ivec2(38,0),0).x*vec2(0,z);
        vec2 right=texelFetch(iChannel1,ivec2(39,0),0).x*vec2(z,0);
        vec2 down=texelFetch(iChannel1,ivec2(40,0),0).x*vec2(0,-z);
        fragColor=vec4(off.xy+left+up+right+down,0.,0.);
    }
}
