vec3 calculateLensFlare(vec2 uv) {
    float flareIntensity = 0.0;

    // Small epsilon to prevent division by zero
    float eps = 0.001;

    // Horizontal flare
    float horizontalFlare = 0.02 / (abs(uv.x) + eps);
    horizontalFlare *= smoothstep(1.0, 0.0, abs(uv.y));

    // Vertical flare
    float verticalFlare = 0.02 / (abs(uv.y) + eps);
    verticalFlare *= smoothstep(1.0, 0.0, abs(uv.x));

    // Diagonal flares
    float diagonalFlare1 = 0.01 / (abs(uv.x + uv.y) + eps);
    float diagonalFlare2 = 0.01 / (abs(uv.x - uv.y) + eps);

    // Combine flares
    flareIntensity = horizontalFlare + verticalFlare + diagonalFlare1 + diagonalFlare2;
    flareIntensity *= exp(-length(uv) * 1.5); // Fade with distance

    // Add color variation to the flare
    vec3 flareColor = vec3(1.0, 0.8, 0.6); // Slightly warm tint
    return flareColor * flareIntensity * 0.3; // Adjust intensity here
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {  

    vec2 uv = (fragCoord * 2. - iResolution.xy) / iResolution.y;

    // Add some movement to make it more interesting
    uv.x += sin(iTime * 0.5) * 0.5;
    uv.y += cos(iTime * 0.3) * 0.3;

    // Le lenz flare
    vec3 color = calculateLensFlare(uv);

    // Add a dark background
    color += vec3(0.1);

    fragColor = vec4(color, 1.0);
}
