#define PI 3.14519

vec3 colMap(float v)
{
  return vec3(sin(sin(v-0.8)),sin(sin(v)),sin(sin(v+0.8)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float u_time = iTime*3.;
 vec2 u_resolution = iResolution.xy;
   vec2 uv = vec2(fragCoord.x/u_resolution.x, fragCoord.y/u_resolution.y)-.5;
	uv.y*=u_resolution.y/u_resolution.x;
	uv*=(-cos(u_time*.22)+1.3)*.4;
	uv=uv.yx*1.;
	uv+=vec2(.1,.615);
	vec2 c =uv;
	vec2 z=c;
	float l=0.;
	float sum=length(z);
	vec2 newZ;
	vec2 gyroscope = vec2(sin(u_time*.58945156489)*1., cos(u_time*.334545642348)*1.);
	for(int i=0; i<5+int(30.*((sin(iTime)+11.4)/2.));i++){
		c+=(gyroscope.yx-10.5)*.008*float(i);
		newZ=vec2(-z.y*z.y+z.x*z.x, 2.*z.x*z.y)+c;
		sum+=length(newZ-z);
		z=newZ;
		l=length(z);
		if(l>2.3+ ((gyroscope.x)-.3)*gyroscope.y) break;
	}
	vec2 dir=z-c;
	vec3 col = vec3(dir, .0);
	uv.x=mod((atan(dir.y,dir.x)/PI*.5+.5)*6.+(u_time+sin(u_time)*46.9)*1., 12.);
	uv.y=mod(length(dir*.5),1.);
	col=colMap(sum*.2-l*.1+u_time*.1).gbr;
    if (iFrame % 10000 < 500) { 
        fragColor = vec4(col.bgr, 1.0);
    } else {
       fragColor = vec4(col.rbg, 1.0);
    }

}
