void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    vec3 image = vec3(texture(iChannel0, fragCoord.xy/iResolution.xy));

    image = pow(image, vec3(1.0 / 2.2));

    fragColor = vec4(image, 1.0);
}

// Define a struct to hold surface information
struct SurfaceInfo {
    vec3 color;
    float distance;
    float radiance;
};

float sdfSphere(vec3 p, float size) {
    return length(p) - size;
}

float sdfPlane(vec3 p, float planeY) {
    return p.y - planeY;
}

float sdfCube(vec3 p, vec3 b) {
    vec3 d = abs(p) - b;
    return min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
}

// Helper function to smoothly combine two SurfaceInfos
SurfaceInfo smoothMinSurface(SurfaceInfo a, SurfaceInfo b, float k) {
    SurfaceInfo result;
    if (k == 0.) {
        result.distance = a.distance;
        result.color = a.color;
        result.radiance = a.radiance;
        if (a.distance > b.distance) {
            result.distance = b.distance;
            result.color = b.color;
            result.radiance = b.radiance;
        }
    }
    float h = clamp(0.5 + 0.5 * (b.distance - a.distance) / k, 0.0, 1.0);

    result.distance = mix(b.distance, a.distance, h) - k * h * (1.0 - h);
    result.color = mix(b.color, a.color, h);
    result.radiance = mix(b.radiance, a.radiance, h);
    return result;
}

// Helper function to smoothly subtract one SurfaceInfo from another
SurfaceInfo smoothSubSurface(SurfaceInfo a, SurfaceInfo b, float k) {
    float h = clamp(0.5 - 0.5 * (b.distance + a.distance) / k, 0.0, 1.0);
    SurfaceInfo result;
    result.distance = mix(a.distance, -b.distance, h) + k * h * (1.0 - h);
    result.color = mix(a.color, b.color, h); // Adjust as needed for subtraction
    result.radiance = mix(a.radiance, b.radiance, h); // Adjust if radiance matters
    return result;
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

// Scene definition
SurfaceInfo map(vec3 p) {
    // k is the smoothing factor
    const float k = 0.;
    SurfaceInfo result;

    const int NUM_CUBES = 6;
    SurfaceInfo cubeSurfaceInfos[NUM_CUBES];
    vec3 cubePositions[NUM_CUBES];
    vec3 cubeSizes[NUM_CUBES];

    // Define Outside Cube
    cubePositions[0] = vec3(0., 0., 0.);
    cubeSizes[0] = vec3(10., 10., 10.);
    cubeSurfaceInfos[0].color = vec3(1.);
    cubeSurfaceInfos[0].radiance = 0.;
    cubeSurfaceInfos[0].distance = sdfCube(p - cubePositions[0], cubeSizes[0]);

    // Define Inside Cube
    cubePositions[1] = vec3(0., 0., 0.);
    cubeSizes[1] = vec3(5., 5., 15.);
    cubeSurfaceInfos[1].color = vec3(1.);
    cubeSurfaceInfos[1].radiance = 0.;
    cubeSurfaceInfos[1].distance = sdfCube(p - cubePositions[1], cubeSizes[1]);

    // Calculate first result by carving the outside cube with the inside one
    result = smoothSubSurface(cubeSurfaceInfos[0], cubeSurfaceInfos[1], k);

    const int NUM_SPHERES = 3;
    SurfaceInfo sphereSurfaceInfos[NUM_SPHERES];
    vec3 spherePositions[NUM_SPHERES];
    float sphereSizes[NUM_SPHERES];

    // Define spheres
    spherePositions[0] = vec3(0.0, -1.0, 0.0);
    sphereSizes[0] = 1.0;
    sphereSurfaceInfos[0].color = vec3(0.99,0.97,0.99); // light source
    sphereSurfaceInfos[0].radiance = 0.0;

    spherePositions[1] = vec3(3.0, 0.0, 1.0);
    sphereSizes[1] = 1.0;
    sphereSurfaceInfos[1].color = vec3(0.0,0.0,0.96); // green sphere
    sphereSurfaceInfos[1].radiance = 0.99;

    spherePositions[2] = vec3(-1.0, 2.0, 1.0);     //red shpere
    sphereSizes[2] = 1.2;
    sphereSurfaceInfos[2].color = vec3(0.82,0.08,0.082);
    sphereSurfaceInfos[2].radiance = 0.99;

    // Calculate distances to the spheres
    for (int i = 0; i < NUM_SPHERES; i++) {
        sphereSurfaceInfos[i].distance = sdfSphere(p - spherePositions[i], sphereSizes[i]);
    }

    // Find the closest surface
    for (int i = 0; i < NUM_SPHERES; i++) {
        result = smoothMinSurface(result, sphereSurfaceInfos[i], k);
    }

    return result;
}


// Function to compute the normal at point p
vec3 computeNormal(vec3 p) {
    const float eps = 0.0001;
    float dx = map(p + vec3(eps, 0.0, 0.0)).distance - map(p - vec3(eps, 0.0, 0.0)).distance;
    float dy = map(p + vec3(0.0, eps, 0.0)).distance - map(p - vec3(0.0, eps, 0.0)).distance;
    float dz = map(p + vec3(0.0, 0.0, eps)).distance - map(p - vec3(0.0, 0.0, eps)).distance;
    return normalize(vec3(dx, dy, dz));
}

// Random number generator
float random(vec2 co) {
    highp float a = 12.9898;
    highp float b = 78.233;
    highp float c = 43758.5453;
    highp float dt = dot(co.xy, vec2(a, b));
    highp float sn = mod(dt, 3.14);
    return fract(sin(sn) * c);
}
vec3 anyOrthogonalVector(vec3 n) {
    // Find a vector that is orthogonal to n
    if (abs(n.x) > abs(n.z)) {
        return vec3(-n.y, n.x, 0.0);
    } else {
        return vec3(0.0, -n.z, n.y);
    }
}

// Function to sample a random direction in a hemisphere oriented around a normal vector
vec3 sampleHemisphereCosine(vec3 normal, vec2 rand) {
    // Convert random numbers to spherical coordinates
    float phi = 2.0 * 3.14159265 * rand.x;
    float cosTheta = sqrt(1.0 - rand.y);
    float sinTheta = sqrt(rand.y);

    // Compute the direction in tangent space
    vec3 tangent = normalize(anyOrthogonalVector(normal));
    vec3 bitangent = cross(normal, tangent);
    vec3 direction = sinTheta * cos(phi) * tangent + sinTheta * sin(phi) * bitangent + cosTheta * normal;

    return normalize(direction);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Compute normalized screen coordinates
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;

    vec3 finalColor = vec3(0.0);

    const int sampleNumber = 1;
    const int bounceTimes = 16;
    const int maxMarches = 150;

    // Loop over the number of samples
    for (int s = 0; s < sampleNumber; s++) {

        // Introduce a small random offset for anti-aliasing
        vec2 jitter = vec2(random(fragCoord + float(s) + 0.1),
                           random(fragCoord - float(s) + 0.2)) / iResolution.xy;
        uv += jitter;

        // Initialize ray origin and direction
        vec3 ro = vec3(0.0, 0.0, -3.0);
        vec3 rd = normalize(vec3(uv, 1.0));

        vec3 accumulatedColor = vec3(0.0);
        vec3 throughput = vec3(1.0);

        // Bounce Loop
        for (int j = 0; j < bounceTimes; j++) {
            float t = 0.0;
            bool hit = false;

            // Ray marching loop
            for (int i = 0; i < maxMarches; i++) {
                vec3 p = ro + rd * t;
                SurfaceInfo a = map(p);
                float d = a.distance;

                if (d < 0.001) {
                    hit = true;

                    // Compute normal at the hit point
                    vec3 normal = computeNormal(p);

                    // Accumulate emitted light
                    vec3 emittedColor = a.color * a.radiance;
                    accumulatedColor += throughput * emittedColor;

                    // Update throughput with the material's albedo
                    throughput *= a.color;

                    // Generate two random numbers for hemisphere sampling
                    vec2 randUV = vec2(random(uv.xy - float(s) + float(j) * 0.1 + iTime),random(iResolution.yx * float(s) + float(j) * 3.14 + iTime));

                    // Sample a random direction in the hemisphere around the normal
                    vec3 randomDir = sampleHemisphereCosine(normal, randUV);

                    // Update ray origin and direction
                    ro = p + normal * 0.001; // Offset to prevent self-intersection
                    d = 20.;
                    rd = randomDir;

                    break; // Proceed to the next bounce
                }

                t += d;

                // Exit if the ray goes too far
                if (t > 1000.0) {
                    break;
                }
            }

            if (!hit) {
                // If the ray didn't hit anything, assume background color (e.g., black)
                break;
            }
        }

        // Accumulate the color from this sample
        finalColor += accumulatedColor;
    }

    // Average the final color over all samples
    finalColor /= float(sampleNumber);

    vec3 prevColor = vec3(texture(iChannel0, fragCoord / iResolution.xy));

    finalColor = mix(prevColor, finalColor, 1.0f / float(iFrame+1));

    fragColor = vec4(finalColor, 1.0);
}
