//from: https://www.shadertoy.com/view/ldBGDc
float spiral(vec2 m) {
    float t = iTime;
	float r = length(m);
	float a = atan(m.y, m.x);
	float v = sin(100.*(sqrt(r)-0.02*a-.3*t));
	return clamp(v,0.,1.);

}
////////////////

float eye(vec2 uv)
{
    vec2 look = (iMouse.xy / iResolution.xy - vec2(0.5)) * 0.5 + vec2(0.0, 0.5);
    float iris = smoothstep(0.09, 0.1, length(uv - look) - 0.22);

    if(iMouse.z > 0.0) iris = mix(spiral(uv - look), 1.0, iris);

    uv.y -= (uv.y < 0.5)? 0.6 : 0.4;

    float t = mod(iTime, 3.0);
    float eye = uv.y * pow(length(uv), 3.0 * ( ( t > 1.0)? 1.0 : pow(sin(t * 1.57 * 3.0), 2.0) ) );
    eye = smoothstep(0.1, 0.09, eye) * smoothstep(-0.1, -0.09, eye);

    return eye * iris;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.yy;
    uv -= vec2(0.88, 0.25);
    uv *= 1.5;

    vec3 col = vec3(eye(uv));

    // Output to screen
    fragColor = vec4(col,1.0);
}
