/*originals https://www.shadertoy.com/view/DtGyWh https://www.shadertoy.com/view/MfBGz3*/
#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(153,33,31))*.5+.5)

#define rot(a)    mat2(cos((a)+vec4(0,11,33,0)))              // rotation


void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
     vec4 O2 = O;
   vec2  U  = C;

    float t=9., l, i, r;
    vec3  R = iResolution, a,
          D = normalize(vec3(U+U, -4.*R.y) - R),              // ray direction
          p = vec3(0,2,15), q,                                // marching point along ray 
          C2 = iMouse.z > 0. ? 8.*iMouse.xyz/R -4.             // camera control
                          : 3.* cos(.3*iTime + vec3(0,11,0)); // demo mode
    vec2 v;
    for ( O2=vec4(1); O2.x > 0. && t > .01; O2-=.01 ) {          // march scene
        q = p, 
        q.yz *= rot(-C2.y),                                    // camera
        q.xz *= rot(-C2.x);
        for (r = 4.; r > .5; r -= 0.25 )                        // for each ring
            q.xz = vec2(-q.z,q),                              // swap axis by PI/2
            q.yx *= rot(clamp( mod(iTime,3.) * 3.14*r, 1.,3.14)), // rotate the ring
            a = abs(q), t = min(t,  max( abs( max(a.x,a.z) - r+.25 ) -.23  , a.y - .2 ) ); // draw frame

        p += 0.7*t*D;                                          // step forward = dist to obj    
    }
    O2 *= O2*O2*1.;   


     vec4 O3 = O;
   vec2  U2  = C;

    float t2=9., l2, i2, r2;
    vec3  R2 = iResolution, a2,
          D2 = normalize(vec3(U2+U2, -4.*R2.y) - R2),              // ray direction
          p2 = vec3(0,2,15), q2,                                // marching point along ray 
          C3 = iMouse.z > 0. ? 8.*iMouse.xyz/R2 -4.             // camera control
                          : 3.* cos(.3*iTime + vec3(0,11,0)); // demo mode
    vec2 v2;
    for ( O3=vec4(1); O3.x > 0. && t2 > .01; O3-=.01 ) {          // march scene
        q2 = p2, 
        q2.yz *= -rot(-C3.y),                                    // camera
        q2.xz *= -rot(-C3.x);
        for (r2 = 4.; r2 > .5; r2 -= 0.25 )                        // for each ring
            q2.xz = vec2(-q2.z,q2),                              // swap axis by PI/2
            q2.yz *= rot(clamp( mod(iTime,3.) * 3.14*r2, 0.,3.14)), // rotate the ring/ rotate the ring
            a2 = abs(q2), t2 = min(t2,  max( abs( max(a2.x,a2.z) - r2+.25 ) -.23  , a2.y - .2 ) ); // draw frame

        p2 += 0.7*t2*D2;                                          // step forward = dist to obj    
    }
    O3 *= O3*O3*1.;  


    vec3 naturals,r3=iResolution,
    d=normalize(vec3((C*2.-r3.xy)/r3.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<110.;
        O.xyz+=mix(vec3(1),H(g*.1),sin(.8))*1./e/8e3
    )
    {
        naturals=g*d;
        naturals.z+=iTime*0.0;
        a=30.;
        naturals=mod(naturals-a,a*2.)-a;
        s=3.;
        for(int i=0;i++<8;){
            naturals=.3-abs(naturals);

            naturals.x<naturals.z?naturals=naturals.zyx:naturals;
            naturals.z<naturals.y?naturals=naturals.xzy:naturals;
            naturals.y<naturals.x?naturals=naturals.zyx:naturals;

            s*=e=1.4+sin(iTime*.234)*.1;
            naturals=abs(naturals)*e-
                vec3(
                    5.+cos(iTime*.3+.5*cos(iTime*.3))*3.,
                    70,
                    3.+cos(iTime*.5)*5.
                 )*O3.xyz+O2.xyz;
         }
         g+=e=length(naturals.yz)/s;

    }

}
