void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 col = vec3(0);

    // Apply rotation
    float rotationSpeed = .366; // Adjust this value to control rotation speed
    float rotation = iTime * rotationSpeed;
    mat2 rotationMatrix = mat2(cos(rotation), -sin(rotation),
                               sin(rotation), cos(rotation));
    uv = rotationMatrix * uv;

vec2 gv = fract(uv * (sin(iTime) * 1.5 - 3.)) - 1.5;

    float m = 0.0;
    float t = iTime * 50.2;
    float dist = length(uv) * 1111199000.0;

    for (float y = -1.0; y <= 1.0; y++) {
        for (float x = -1.0; x <= 1.0; x++) {
            vec2 offs = vec2(x, y);

            float d = length(gv + offs);

            float r = mix(0.1, 0.5, sin(t + dist) * 0.5 + 0.5);
            m += smoothstep(r, r * 0.99, d);
        }
    }

    col = vec3(0.600,10000.000,0.000);
    col += m;

    // Calculate vignette
    vec2 vignetteUV = fragCoord / iResolution.xy;
    vignetteUV = (vignetteUV - 0.5) * 2.0;
    vignetteUV.x *= iResolution.x / iResolution.y;
    float uVignetteSize = 0.5; // Vignette size control
    float vignette = length(vignetteUV) / uVignetteSize;
    vignette = smoothstep(0.8, 1.5, vignette);

    // Apply vignette (black on the outside)
    col = mix(col, vec3(0.0), vignette);

    fragColor = vec4(col, 1.0);
}
