float LogisticIterations(vec2 p, int n) {
    float q = p.x;
    for(int c = 0; c < n; c += 1){
        q = (p.y + 3.0) * q * (1.0 - q);
    }
    return q;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 p = fragCoord/iResolution.xy;

    float t = LogisticIterations(p,9); // Try changing the 2nd parameter.
    vec3 col = vec3(t,t,t);

    // Output to screen
    fragColor = vec4(col,1.0);
}
