void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 mycell = texture(iChannel1,vec2(uv.x,0));

    float k = mycell.z + 0.5 - uv.y;
    float c1;
    c1 = clamp(k*100.,0.,1.);
    //if (k<0.0) c1=1.0;

    //float f  = k/5.;    
    //float df = fwidth(k );    
    //float c = smoothstep(df * -1.0, df * 0., f);
    //float c1= (1.0-smoothstep(df * 0.0, df*1.0, f))*c;
    fragColor = vec4(c1/10., c1, c1, 1.0);
    if (length(fragCoord - iMouse.xy) < 6.1) fragColor = vec4(1,0,0,1);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0,0.0,1.0,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 UM =       1.0/iResolution.xy;
    vec4 inc = texture(iChannel1,uv);
    vec4 inc0= texture(iChannel1,uv-UM.x);
    vec4 inc1= texture(iChannel1,uv+UM.x);
    float c;

    if (iFrame < 1) {
        c = smoothstep(0.0,1.0, 1.0-abs((uv.x-0.5)*20.))*0.1;
        c -= smoothstep(0.0,1.0, 1.0-abs((uv.x-0.4)*13.))*0.1;
        c += smoothstep(0.0,5.0, 5.0-abs((uv.x-0.2)*30.))*0.1;
        fragColor = vec4(0,0,0,0);
    } else {

        // z = displacement
        // y = velocity
        // compute force.
        float f =
             (inc0.z - inc.z) * iC
            +(inc1.z - inc.z) * iC
            +(-inc.z) * sC;
        f += clamp(1.0-pow((fragCoord.x - iMouse.x)/10.,2.0),0.,1.0)*0.0004;
        inc.y += f;
        inc.z += inc.y;


        fragColor = vec4(inc);
    }
}
