#define Res iResolution

#define Niter 10.
#define Scale 2.
#define c 0.05
#define d 0.1
#define v 2.
#define u 1.43

vec2 cmul(vec2 w, vec2 z) {
    return vec2(w.x * z.x - w.y * z.y, w.x * z.y + w.y * z.x);
}

vec2 F(vec2 z, vec2 r) {
    return 1.01*sin(vec2(0, 1.) + cmul(z, r - z));
}

void mainImage(out vec4 Y, in vec2 fc) {
    vec2 z0 = Scale * (fc - Res.xy / 2.) / Res.y;
    vec2 z = z0;
    vec2 r = (1. + d * sin(iTime * u)) *
              Scale * (iMouse.xy - Res.xy / 2.) / Res.y;
    r += c * vec2(cos(iTime * v), sin(iTime * v));
    for (float i = 0.; i < Niter; i += 1.) z = F(z, r);
    vec2 s = F(z, r);
    vec2 w = z0;
    for (float i = 0.; i < Niter; i += 1.) w = F(w, s);
    Y = vec4(z.x * w.y, z.x * w.x, z.y * w.x, 1.);
}
