void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float head = sin(35.0*(1.0-Circle(uv, vec2(-0.8, 0.0), 0.45, 0.03))+iTime);
    float head2 = sin(35.0*(1.0-Circle(uv, vec2(0.8, 0.0), 0.45, 0.03))+iTime);
    float result = head+head2;

