#define Pi  3.1415926
vec2 CenterUV(vec2 fragCoord )//uv的居中与反拉伸https://www.shadertoy.com/view/M3cSDS
{
    return (2.0 * fragCoord - iResolution.xy) / min(iResolution.x, iResolution.y);
}
vec2 PolarUV(vec2 uv)//直角坐标转极坐标https://www.shadertoy.com/view/XfsyDM
{
    return vec2(atan(uv.x,uv.y),length(uv));
}
float CrateGrid(vec2 uv,float cellSize)//创建网格https://www.shadertoy.com/view/4fXyzr
{
    vec2 line = vec2(0.005)*uv.y;
    uv = abs(mod(uv - 0.5 * cellSize,cellSize)-0.5 * cellSize);
    vec2 gridUV= 1.- pow(uv,vec2(.4));
    gridUV += 1.- (smoothstep(line-0.01, line +0.01,uv));
    return gridUV.x * gridUV.y;
}
vec3 xyz(vec2 uv)
{
    float z = 1./abs(uv.y);
    float x = uv.x/(Pi*2.)-.5;
    return vec3(x,uv.y,z);
}
vec2 RotateUV(vec2 uv,float angle)//uv旋转
{
    return vec2(uv.x * cos(angle) - uv.y * sin(angle),uv.x * sin(angle) + uv.y *cos(angle));
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = CenterUV(fragCoord );
    uv = PolarUV(uv);
    vec3 xyz = xyz(uv);

    vec3 col = 0.5 + 0.5*cos(iTime+vec3(xyz.y,xyz.y,xyz.z)+vec3(1.,0.,3.));

    vec2 gridUV = vec2(xyz.x*6.+iTime-xyz.y*2.,xyz.z-xyz.y);
    gridUV.y -= gridUV.x;
    float grid = CrateGrid(gridUV,.4) *(1.-smoothstep(2.,5.-sin(iTime*gridUV.x),xyz.z)) ;
    vec3 gridColor = grid * col;

    fragColor = vec4(gridColor,1.);
}
