vec3 blackWhite(in vec3 color)
{

    float threshold = 0.4;
    vec3 tint = vec3(1.0, 1.0, 1.0);
    bool flip = false;
    float brightness = (color.r + color.g + color.b) / 3.0;
    float thresholded = mix(0.0, 1.0, step(threshold, brightness));
    if (flip){
        thresholded = 1.0 - thresholded;
    }
    return vec3(thresholded) * tint;

}

vec4 radialBlur(in vec2 fragCoord)
{
    int nsamples = 20;
    vec2 center = iMouse.xy /iResolution.xy;
    float blurWidth = 0.13;
    float blurStart = 1.0 - blurWidth;


	vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= center;
    float precompute = blurWidth * (1.0 / float(nsamples - 1));

    vec4 color = vec4(0.0);
    for(int i = 0; i < nsamples; i++)
    {
        float scale = blurStart + (float(i)* precompute);
        color.rgb += blackWhite(texture(iChannel0, uv * scale + center).rgb);
    }


    color /= float(nsamples);

	return color;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   fragColor = radialBlur(fragCoord);
}
