void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = iTime;

    float x = uv.s * 2.0 - 1.0;
    float y = uv.t * 2.0 - 1.0;
    x *= iResolution.x/iResolution.y;

    vec3 color = vec3(0.);
    float r = sqrt(x*x + y*y);
    r += 0.08 * sin(time*4.0) * sin(atan(y, x) * 6.0 + time * 0.5);
    color.r = pow(sin(r * 1.2),90.2) * sin(r * mix(30.0, 100.0, 0.5+0.5*cos(atan(y, sin(x+time))/3.14159 * mix(3.0,12.0,0.5+0.5*sin(time)))));
    color.g = pow(sin(r * mix(2.0, 3.0, sin(time*0.55))),22.2) * sin(r * mix(30.0, 100.0*sin(time*0.7), 0.5+0.5*cos(atan(y, sin(x+time))/3.14159 * mix(4.0,12.0,0.5+0.5*sin(time)))));
    color.b = pow(sin(r * 4.0),10.7) * sin(r * mix(30.0, 100.0, 0.5+0.5*cos(atan(y, sin(x+time))/3.14159 * mix(4.0,12.0,0.5+0.5*sin(time)))));
    color.rgb *= 7.0 * sin(r * 5.0 + time) * sin(r * mix(20.0, 10.0, 0.5+0.5*cos(atan(y, sin(x+time))/3.14159 * mix(4.0,12.0,0.5+0.5*sin(time)))));

    fragColor = vec4(color,1.0);
}
