void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

  vec4 pframe = texture(iChannel0,fragCoord.xy/iResolution.xy);

    // Output to screen
    fragColor = vec4(min(pframe.xyz/pframe.a,vec3(1.,1.,1.)),1.);
}

mat3 orth(vec3 p){
    vec3 z = normalize(p);
    vec3 x = vec3(z.z,0.,-z.x);
    return mat3(x,cross(z,x),z);
}
vec3 erot(vec3 p,vec3 ax,float t){return mix(dot(ax,p)*ax,p,cos(t))+cross(ax,p)*sin(t);}
vec3 smin3(vec3 a,vec3 b,float h){
    vec3 k = clamp((a-b)/h*.5+.5,0.,1.);
    return mix(a,b,k)-k*(1.-k)*h;
}
float smin(float a,float b,float h){
    float k = clamp((a-b)/h*.5+.5,0.,1.);
    return mix(a,b,k)-k*(1.-k)*h;
}
vec3 pcg3d(vec3 p){
    uvec3 q = floatBitsToUint(p)*1234567890u+12345678u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    q^=q>>16u;
    q.x+=q.y*q.z;q.y+=q.x*q.z;q.z+=q.y*q.x;
    return vec3(q)/float(-1U);
}
vec3 kifs(vec3 p,float t,float z){
    float s = 5.;
    for(int i=0;i<3;i++){
        float t2 = t +float(i);
        p = erot(p,normalize(vec3(1,-.0,.0)),t2*.785);
        p = smin3(p,-p,-2.0)-step(.5,z);
        p.xz = p.z>p.x ? p.zx:p.xz;
        p-=s;
        s*=.3;
    }
    return p;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy -.5* iResolution.xy)/iResolution.y;

    vec3 rnd = pcg3d(vec3((uv.xyy*50.)));
vec3 col = vec3(0.);
    vec3 ro=vec3(0.,0.,-50.),rt=vec3(0.);
    ro=erot(ro,normalize(vec3(0.,1.,0)),iTime);
    vec3 rd = orth(rt-ro)*normalize(vec3(uv,1));

    for(float i=0.,e=0.,g=0.;i++<99.;){

        vec3 p = ro+rd*g;
        float t = iTime+rnd.x*.1+p.y*.01;
        t = floor(t)+smoothstep(.0,1.,pow(fract(t),.17));
             vec3 trnd = pcg3d(vec3(floor(t)));
        vec3 p1 = kifs(p,2.+t,trnd.y);
        vec3 p2 = kifs(erot(p+vec3(1.7,2.2,2.5),normalize(vec3(.0,1.,0.)+trnd),.785),3.+t,trnd.z);
        float h = smin(abs(min(min(length(p1.yz),length(p1.xz)),length(p1.xy))),
                       abs(min(min(length(p2.yz),length(p2.xz)),length(p2.xy))),-1.0)-1.15;
        g+=e=max(.001,abs(h)-.1*step(.5,fract(p1.y-p2.z)));
        col +=
        normalize(sin(mix(vec3(.5,.8,.9),vec3(1.,.5,.2),step(.5,fract(p1.y-p2.z))))+trnd)*.0515/exp(.5*i*i*e);

    }
    col = mix(vec3(1.0)-sqrt(length(uv)),col,smoothstep(.01,.1,length(col)));

    vec4 pframe = texture(iChannel0,fragCoord.xy/iResolution.xy);
    col = mix(fwidth(col)+col,pframe.xyz*.9,.5);
    fragColor = vec4(col,1.0);
}
