//Original Shader by haiku
#define t iTime
#define res iResolution.xy

float hash(float n) {
    return fract(sin(n) * 43758.5453);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec3 col = vec3(0.0);
    vec2 uv = fragCoord / res;
    uv = uv * 2.0 - 1.0;
    uv.x *= res.x / res.y;

    vec2 mouse = iMouse.xy / res;
    float zoom = t * 0.5 + mouse.x * 5.0;
    float rotation = mouse.y * 6.28;

    // Create a pulsing effect
    float pulse = sin(t * 2.0) * 0.5 + 0.5;

    for (int i = 0; i < 3; i++) {
        float s = sin(rotation), c = cos(rotation);
        vec2 rotatedUV = vec2(uv.x * c - uv.y * s, uv.x * s + uv.y * c);

        // Create a more organic swarm effect
        vec2 swarmOffset = vec2(
            sin(zoom + rotatedUV.x * 4.0 + hash(float(i)) * 6.28),
            cos(zoom + rotatedUV.y * 4.0 + hash(float(i) + 1.0) * 6.28)
        );

        rotatedUV += swarmOffset * (0.2 + pulse * 0.1);

        float lum = 0.15 / (length(mod(rotatedUV * (8.0 + pulse * 2.0), 1.0) - 0.5) + 0.01);

        // Create a dark purple color scheme
        float hue = 0.75 + hash(float(i)) * 0.1; // Purple hue range
        float sat = 0.8 + hash(float(i) + 2.0) * 0.2;
        float val = lum * (0.5 + pulse * 0.2);

        // Convert HSV to RGB
        vec3 rgb = clamp(abs(mod(hue * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
        rgb = val * mix(vec3(1.0), rgb, sat);

        col += rgb * val;
    }

    // Enhance contrast and add a subtle vignette
    col = pow(col, vec3(1.2));
    float vignette = 1.0 - length(uv) * 0.5;
    col *= vignette;

    fragColor = vec4(col, 1.0);
}
