#define t iTime

float grid(vec2 p) {
    float l = length(p), 
          a = cos(t * .2) * 7. + 8.,  
          w = 3.14/a;

    p = vec2(
            log(l),
            atan(p.y, p.x)
        );

    p = mod(p, w) - w/2.;

    return abs(length(p) - .098 * (16. / a)) * l;
}

void mainImage(out vec4 o, vec2 u) {
    vec2 r = iResolution.xy; 
         u = vec2(u + u - r) / r.y;

    o = smoothstep( 3. / r.y, .0, grid(u)) * vec4(1);
}
