// from The Book of Shaders
float random() {
    return fract(sin(iTime - 12.9898*78.233) * 43758.5453123);}
float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);}

vec3 brighten(vec3 color, float percent) {
    return color + color * percent;
}

vec3 sway(vec3 bg_col,vec2 pos) {
    vec2 delta = round(
        vec2(random(pos)*.7, random(pos)*4.) *              // the initial vector for wind
        (sin(iTime * 0.5 - pos.x*2./iResolution.x)+1.25 )); // amount that the grass should sway by. 0 is very jarring
    return brighten(bg_col, random(pos + delta)*.8);
}




void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = floor(fragCoord / 2.); 
    vec3 col = vec3(.3, .5, .05);
    col = sway(col, pos);

    fragColor = vec4(col, 1.0);
}
