void mainImage(out vec4 RGBA, in vec2 COORDS)
{
    vec2 R = iResolution.xy,
         u = (COORDS - R/2.) / R.y*6.,
         w = fwidth(u); // pixel size

    vec3 c = vec3(0, 1, 2) / 20.; // background color

    //u = dot(u, u) / u; // coord transform

    c.r += .01 / abs(u.y - sin(u.x)); // graph line

    float t = iTime,
          o = t + round(u.x - t); // tile x & offset

    u.x -= o;
    u.y -= sin(o); // graph movement
    u.y -= round(u.y); // tile y
    c += max(.01, w.x) / length(u); // draw points (limit smallest size)

    RGBA = vec4(c, 1);
}
