float sphere(vec2 coord, vec2 pos, float radius)
{
    float result = distance(coord, pos);
    result = smoothstep(radius * 0.95, radius, result);
    return 1.0 - result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 aspCoord = uv * 2.0 - 1.0;

    aspCoord.x *= iResolution.x / iResolution.y;

    float sphere_radius = 0.5;
    vec3 sphere_Col = vec3(0.4, 0.6, 1.0);

    float sphere1 = sphere(aspCoord, vec2(-0.8, 0.0), 0.6);
    float sphere2 = sphere(aspCoord, vec2(0.8, 0.0), 0.6);

    vec3 col = vec3(0.0);

    col += sphere1 * sphere_Col;
    col += sphere2 * sphere_Col;

    float side = step(0.0, aspCoord.x);

    vec3 bgColor_left = vec3(0.85, 0.5, 0.3);
    vec3 bgColor_right = vec3(0.5, 0.85, 0.3);

    float side_left = side;
    float side_right = 1.0 - side;

    col += (side_left - sphere2) * bgColor_right;
    col += (side_right - sphere1) * bgColor_left;

    vec2 scaleCoord = fract(aspCoord * 10.0);
    scaleCoord = scaleCoord * 2.0 - 1.0;

    float minSpere = length(scaleCoord);
    minSpere = smoothstep(0.6, 0.7, minSpere);
    minSpere = 1.0 - minSpere;

    float factor_left = side_right * minSpere;
    float factor_right = side_left * minSpere;

    float animate = step(0.0, sin(iTime));
    factor_left *= animate;
    factor_right *= animate;

    col = mix(col, factor_left * bgColor_right, factor_left);
    col = mix(col, factor_right * bgColor_left, factor_right);

    // Output to screen
    fragColor = vec4(col,1.0);
}
