void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 perpandicularcol =  0.5 + 0.5*cos(10.0*iTime+40.0*uv.yyy);
    vec3 col = 0.5 + 0.5*cos(10.0*iTime+40.0*uv.xxx);

    // Output to screen
    fragColor = vec4(col+perpandicularcol,1.0);
}
