#define PI 3.1415

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float blur = 2.0 / iResolution.y;

    // normalized pixel coordinates
    vec2 uv = (fragCoord*2.0-iResolution.xy)/iResolution.y;

    uv.xy = uv.yx;

    // animation
    float t = 2.0 * PI * fract(iTime); // for gazing
    //float t = 2.0 * PI * (iTime); // for recording

    float a = 0.0;

	if (uv.y >= 0.0) a =      atan(uv.y,  uv.x);
    if (uv.y <  0.0) a = PI - atan(uv.y, -uv.x);

    // donut
    float d0 = length(uv);
    float d1 = smoothstep(d0 - blur, d0 + blur, 1.0);
    float d2 = -smoothstep(d0 - blur, d0 + blur, .42);

    vec3 col = vec3(-smoothstep(a - blur, a + blur, t) + d1 + d2);

	fragColor = vec4(col, 1.0);
}
