// ShaderToy simulation of dynamic electric and magnetic fields around a moving charged particle.
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Coordinate setup
    vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x / iResolution.y;

    // Time-dependent animation
    float time = iTime * 2.0;

    // Central charge position
    vec2 center = vec2(0.0, 0.0);

    // Distance from charge
    float dist = length(uv - center);

    // Electric field: Radial lines from the center
    float electricField = sin(10.0 * atan(uv.y - center.y, uv.x - center.x) + time) / dist;

    // Magnetic field: Circular loops around the center
    float magneticField = sin(20.0 * dist - time);

    // Combine fields with color representation
    vec3 color = vec3(
        0.5 + 0.5 * sin(electricField * 10.0), // Electric field (red component)
        0.4 + 0.4 * sin(magneticField * 10.0), // Magnetic field (green component)
        0.7 + 0.7 * cos((electricField + magneticField) * 5.0) // Interaction (blue component)
    );

    // Output the color
    fragColor = vec4(color, 1.0);
}
