vec2 applySphericalDistortion(vec2 uv,float radius)
{
  vec2 cc=uv-0.5;
  float dist=dot(cc,cc);
  float z=sqrt(max(radius*radius-dist,0.0));
  vec2 distortedUV=uv+cc*z;return distortedUV;
}

vec3 gridPattern(vec2 coords)
{
  float lineWidth=0.02;
  vec2 grid=fract(coords*10.0);
  float line=step(grid.x,lineWidth)+step(grid.y,lineWidth);
  line+=step(1.0-grid.x,lineWidth)+step(1.0-grid.y,lineWidth);
  return vec3(line);
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
  vec2 uv=fragCoord/iResolution.xy;
  uv=applySphericalDistortion(uv,0.5*abs(sin(iTime)));
  vec3 color=gridPattern(uv);fragColor=vec4(color,1.0);
}
