void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float n_scl_min = min(iResolution.x, iResolution.y);
    vec2 o_trn = (fragCoord.xy-iResolution.xy*.5)/n_scl_min;
    // o_trn+=vec2(
    //     sin(iTime*.2),
    //     cos(iTime*.2)
    // );
    o_trn*= sin(iTime*.2)*2.;
    float nt = iTime*2.;
    vec2 o_trn2 = o_trn*20.;
    vec2 o_trn2flr = floor(o_trn2);
    vec2 o_trn2frc = fract(o_trn2)-.5;
    float n = length(o_trn2frc);
    float n_d_cntr = length(o_trn);
    float n_tau = radians(360.);
    float n_t = iTime;
    float n_offset = n_d_cntr*.3;
    vec3 o_t = vec3(
        n_t+n_offset*1.,
        n_t+n_offset*2.,
        n_t+n_offset*3.
    );
    vec3 o_radius = vec3(
        sin(n_d_cntr*3.*n_tau+o_t[0])*.35,
        sin(n_d_cntr*3.*n_tau+o_t[1])*.35,
        sin(n_d_cntr*3.*n_tau+o_t[2])*.35
    );
    vec3 o_n2 = vec3(
        sin(n_d_cntr*3.*n_tau+o_t[0])
    )*.5+.5;
    o_n2[0] = 1.-pow(o_n2[0], 1./20.);
    o_n2[0]*=2.;
    vec3 o_n = 1.-vec3(
        abs(n-o_radius[0]),
        abs(n-o_radius[1]),
        abs(n-o_radius[2])
    );
    o_n[0] = pow(o_n[0], 3.);
    o_n[1] = pow(o_n[1], 3.);
    o_n[2] = pow(o_n[2], 3.);
    vec3 o_col = vec3(
        o_n
    );
    o_col *= o_n2[1];
    o_col += o_n2[0];
    fragColor = vec4(o_col, 1.);
}
