const vec2 screen = vec2( 1200, 675 );
const float radius = 400.;

struct Ball {
    vec2 pos;
    float radius;
    vec2 vel;
    float speed;
};

Ball balls[] = Ball[](
    Ball( vec2( 20,  220 ),  800.  , vec2( .1, .9 ), 2.1 ),
    Ball( vec2( 60,  140 ),  400.  , vec2( .3, .7 ), 1.3 ),
    Ball( vec2( 140, 10 ),   900.  , vec2( .3, .4 ), 2.2 ),
    Ball( vec2( 440, 90 ),   1400. , vec2( .4, .5 ), 2.6 ),
    Ball( vec2( 230, 350 ),  1500. , vec2( .2, .8 ), 1.9 ),
    Ball( vec2( 430, 60 ),   2300. , vec2( .2, .7 ), 2.7 ),
    Ball( vec2( 110, 90 ),   900.  , vec2( .4, .6 ), 1.4 ),
    Ball( vec2( 90,  70 ),   1000. , vec2( .2, .5 ), 10.4 ),
    Ball( vec2( 350, 1400 ), 2300. , vec2( .1, .9 ), 3.1 )
);


//for ( int i = 0; i < numBalls; i++ ) {
//    balls[ i ] = Ball( vec2( 20, 20 ), 100. );
//}

vec3 renderBall( Ball ball, vec2 fragCoord ) {
    float dist = pow( ball.pos.x - fragCoord.x, 2. ) +
                 pow( ball.pos.y - fragCoord.y, 2. );

    float pc = 1. - ( dist / ball.radius );

    if ( pc <= 0. )
        return vec3( 0. );

    pc = 1. - clamp( pc, 0., 1. );

    return vec3( pc );
}

void simulateBall( int idx ) {
    balls[ idx ].radius += ( 1. + sin( iTime ) ) * 1000.;

    balls[ idx ].pos = balls[ idx ].vel * balls[ idx ].speed; //vec2(
    //    ( 1. + sin( iTime * balls[ idx ].speed * balls[ idx ].vel.x ) ) * screen.x / 2.,
    //    ( 1. + sin( iTime * balls[ idx ].speed * balls[ idx ].vel.y ) ) * screen.y / 2.
    //);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 backgroundColor = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));    

    for ( int i = 0; i < balls.length( ); i++ ) {
        simulateBall( i );
        backgroundColor += renderBall( balls[ i ], fragCoord );
    }

    fragColor = vec4( backgroundColor, 1. );
}
