void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.0); 
    float feq = 0.05;
    float feqt = floor(iTime / feq)*feq;
    //float i = (feqt + 10000.0);
    //float i = (feqt + 10000.0)*feqt; 
    //float i = (feqt + 10000.0)*feqt*feqt*100.0;
    float i = 5000.0+100.0*cos((0.7*feqt));
    col = col+ 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    if (uv.y<0.5+0.5*cos(i*uv.x*uv.y) ||  uv.y>0.5+0.5*sin(i*uv.x*uv.y)
    ||uv.x<0.5+0.5*cos(i*uv.x*uv.y) ||  uv.x>0.5+0.5*sin(i*uv.x*uv.y))
        {
            col = vec3(0.5+0.5*cos(uv.x));
        }
    fragColor = vec4(col,1.0);
}
