float sdf(vec3 p) {
    vec3 position = vec3(0.0, 0.0, -1.8);
    vec3 t = p - position;
    return length(t) - cos(dot(normalize(t), vec3(cos(iTime * .3), 1.0 + sin(iTime * .4), cos(iTime * .7))) * 4.5) - 1.0;
}

vec3 color(vec3 p, vec3 lookat) {
    vec3 lightDir = vec3(1.0, -1.0, 2.5 * sin(iTime));
    vec3 normal = normalize(vec3((sdf(p + vec3(0.0001, 0.0, 0.0)) - sdf(p)) / 0.0001, (sdf(p + vec3(0.0, 0.0001, 0.0)) - sdf(p)) / 0.0001, (sdf(p + vec3(0.0, 0.0, 0.0001)) - sdf(p)) / 0.0001));
    vec3 ambient = vec3(0.2, 0.2, 0.24);
    vec3 diffuse = 0.5 * vec3(1.0) * max(0.0, dot(-normalize(lightDir), normal));
    vec3 specular = .75 * vec3(0.8, 0.9, 1.0) * pow(max(0.0, dot(normal, normalize(-normalize(lookat) - normalize(lightDir)))), 64.0);
    vec3 metallic = texture(iChannel0, reflect(lookat, normal)).rgb * vec3(.70) * (pow(1.0 - abs(dot(normalize(lookat), normal)), 1.0));
    return ambient + diffuse + specular + metallic;
}

vec3 march(vec3 p, vec3 t) {
    for (int i = 0; i < 30; ++i) {
        float d = sdf(p);
        if (d < 0.001) {
            return color(p, t);
        }
        p += normalize(t) * d;
    }
    return vec3(0.0);
}void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord / iResolution.xy * 2.0 - 1.0) * vec2(iResolution.x / iResolution.y, 1.0);
    vec3 camera = vec3(0.0, 0.0, 1.0);
    vec3 lookat = vec3(uv, 0.0) - camera;
    vec3 col = march(vec3(0.0), lookat);
    fragColor = vec4(vec3(smoothstep(0.4, 0.5, col.r)), 1.0);
}
