#define rot(x) mat2(cos(x+vec4(0,11,33,0)))

//Rodrigues-Euler axis angle rotation
#define ROT(p,axis,t) mix(axis*dot(p,axis),p,cos(t))+sin(t)*cross(p,axis)

//formula for creating colors;
#define H(h)  (  cos(  h * vec3(1,2,3)   )*.6 + .5 )

#define H2(h)  (  cos(  h + vec3(1,2,0)   )*.7 + .2 )

//formula for mapping scale factor 
#define M(c)  log(1.+c)

#define R iResolution


void mainImage( out vec4 O, vec2 U) {

    O = vec4(0);

    vec3 c=vec3(0);
    vec4 rd = normalize( vec4(U-.5*R.xy, .5*R.y, R.y))*800.;

    float p1 = 0.; 
    float sc,dotp,totdist=0., tt=p1/3., t=0.;

    for (float i=0.; i<100.; i++) {

        vec4 p = vec4( rd*totdist);

        p += vec4(100.*cos(iTime/17.),250,-20.-100.*sin(iTime/17.),0); 

        p.xz *= rot( tt/2. + abs(sin(tt/3.)) +iTime/7.);

        p.yzw = p.xyz;

        sc = 1.; 

        p.zw *= rot(iTime/5. );


        vec4 w = p;

        for (float j=0.; j<8.; j++) {

            p = abs(p)*.65;

            dotp = max(1./dot(w,w),.004);
            sc *= dotp*(1.2+.05*sin(iTime*2.+sin(iTime))); 

            p = p * dotp - .65; 

            w = .72*log(1.+log(1.+log(1.+p)*p)*p) - .1*cos(p)*sin(p/2.);


        }

        float dist = abs( length(p)-.1)/sc ;  //funky distance estimate
        float stepsize = dist / 20. ;     
        totdist += stepsize;                  //move the distance along rd

        //accumulate color, fading with distance and iteration count

        if (i>15.)
        c +=  (    min(.18,max(.04,(.2*cos(iTime*2.)+.065)))  )*

           ( H2(  atan( p.w, p.z )) 
             +  mix( vec3(1), H(M(sc)),.95)
         ) 
         *  exp(-i*i*stepsize*stepsize*1e1);
    }

    c = 1. - exp(-c*c);
    O = ( vec4(c,0) );

}
