void mainImage( out vec4 c, in vec2 p ) {
    vec2 R = iResolution.xy/2.;
    p -= R;
    p /= 8.;
    p += R;
    p = floor(p);
    ivec2 x = ivec2(p);
    vec4 u = texelFetch(iChannel0, x, 0);
    c = (u.xxxx + 1.) / float(RC+3);
    vec2 d = abs(u.yz-p);
    if ( max(d.x, d.y)< .5 ) {
        c = vec4(1);
    }
}

const float ru[] = float[](3.,1.);
const vec2 mo[] = vec2[](vec2( 1, 0),vec2( 0, 1),vec2(-1, 0),vec2( 0,-1));
const int RC = ru.length();
const int MC = mo.length();

void mainImage( out vec4 c, in vec2 p ) {
    p = floor(p); 
    ivec2 x = ivec2(p);
    vec4 u = texelFetch(iChannel0, x, 0);
    c = u;
    float a = texelFetch(iChannel0, ivec2(c.yz), 0).x;
    vec2 d = abs(u.yz-p);
    if ( max(d.x, d.y) < 1. ) {
        c.x = mod(u.x+19., float(RC));
    }
    c.w += ru[int(a) % RC];
    c.yz = c.yz + mo[int(c.w) % MC];
    if (iFrame == 0) { 
        c = vec4( 0, iResolution.xy/2. , 2 );
    }
}
