float PI = 3.14159265359,
    arms = 10.,
   speed = 1.5,
   width = .4,
subdivide = 8.;

vec4 color = vec4(1.,.4,.1,0.);

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = ( fragCoord - .5* iResolution.xy ) / iResolution.y;
    vec2 mn = ((uv.xy + iMouse.xy) * 0.5) / iResolution.xy;
    uv *=20.0 * mn.y;

    float len = length(uv),
   		angle = (atan(uv.x,uv.y)/(2.*PI))+1.5,
    	wobble = 6.+4.*cos(iTime/5.),
    	white = fract((angle)*arms+sin((sqrt(len)*wobble)-iTime*speed));

    white  = 2.*sin(white/(PI/10.));
    white *= floor(fract(angle*arms+sin(iTime/speed-(len*1.2)*wobble))*subdivide)/subdivide;

    fragColor = smoothstep(0.,1.,white*color*iMouse.x/2.);

    vec4 levels = vec4(4,8,16,1);
    fragColor = floor(fragColor*levels)/levels;
}
