vec4 photoshop_desaturate(vec4 color)
{
    float bw = (min(color.r, min(color.g, color.b)) + max(color.r, max(color.g, color.b))) * 0.5;
    return vec4(bw, bw, bw, color.a); // Preserve alpha channel
}

vec4 sepia(vec4 color)
{
    vec3 c = color.rgb;
    vec3 sepiaColor = vec3(
        dot(c, vec3(0.393, 0.769, 0.189)),
        dot(c, vec3(0.349, 0.686, 0.168)),
        dot(c, vec3(0.272, 0.534, 0.131))
    );
    return vec4(sepiaColor, color.a);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;

    vec4 image = texture(iChannel0, uv);

    vec4 bnw = photoshop_desaturate(image);

    vec4 sepiaColor = sepia(bnw);

    fragColor = sepiaColor;
}
