void mainImage(out vec4 O, vec2 F) {
    vec2 r = iResolution.xy, u = (F+F-r)/r.y;    
    O.rgb*=0.;
    for (float i; i<20.; i++) {
        float circleDistance =.004/(abs(length(u)-i*.04)+.005);
        vec3 color = (tan(i+vec3(0,1.5,2.5))+1.);
        float animation = smoothstep(.35,.4,abs(abs(mod(iTime,2.)-i*.1)-1.));
        O.rgb += circleDistance * color * animation;
        u *= mat2(cos((iTime+i++)*1.02 + vec4(1,35,110,10)));
        u -= vec2(tan(iTime+i*.1), tan(iTime+i*.2))*.1;
    }
}
