float waveFunc(in float num, in float offset) {
    for (int i = 0; i < 5; i++) {
        num = sin(num+float(i)+iTime+float(i)*0.5);
    }
    return num;
}

vec4 makeWave(in vec2 uv, in vec4 inCol, in float magnitude, in float period, in float height, in float speed) {
    vec4 col = vec4(0.0);
    float waveDiff = uv.y - waveFunc(uv.x*period+iTime*speed - period*2000.0, magnitude)*magnitude-height;
    if (waveDiff < 0.0) {
        col = vec4(1.0+waveDiff*2.0, 0.5+waveDiff*2.0, 0.3+waveDiff*0.2, 1.0);
    } else {
        col = inCol;
    }

    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 sunspot = uv*2.0-1.0;
    sunspot.y -= 0.1;
    vec4 col = vec4(1.0,0.5,0.6,1.0);

    vec3 sun = (1.0-length(sunspot)*50.0)*vec3(1.0,1.0,1.0);
    vec3 sunGlo = (1.0-length(sunspot)+abs(abs(sunspot.x)-abs(sunspot.y))*(15.0+10.0))*vec3(1.0,0.5,0.0);
    vec3 sunGlo2 = (1.0-length(sunspot))*vec3(1.0, 0.5, 0.0);
    vec4 sunFull = vec4(sun + sunGlo, 1.0);
    sunFull = clamp(sunFull, 0.0, 1.0);
    col += sunFull*2.0;

    for (int i = 0; i < 20; i++) {
        col = makeWave(uv, col, 0.025+0.025*float(i), 50.0-float(i)*2.5, 0.5-float(i)*0.020, 2.0 + float(i)*0.5)*0.95;
        if (i == 18) {
            col += vec4(sunGlo2, 0.0);
        }
    }


    // Output to screen
    fragColor = vec4(col);
}
