    {
        vec2 n = N22(vec2(i));
        vec2 p = sin(n*t);
        float d = length(uv - p);
        //m += smoothstep(.02, .01, d);
        if(d<minDist){minDist=d;cell=i;}
    }
*/
    gv = fract(uv*10.)-.5;
    id = floor(uv*10.);

    for(float y = -1.; y <=1.; y++)
    {
        for(float x = -1.; x <=1.; x++)
        {
            offs = vec2(x, y);
            n = N22(vec2(id + offs));
            p = offs+sin(n*t)*.5;
            p-=gv;
            d1 = length(p);
            //d2 = abs(p.x) + abs(p.y);
            //d3 = mix(d1, d2, .5);
            if(d1<minDist){minDist=d1;cellIn=id+offs;}

        }
    }
    col = vec3(minDist);
    //col.rg = cellIn*.1;
    col*=vec3(1.,0.,0.);

    O = vec4(col, 1.0);
}

vec2 N22(vec2 p)
{
    vec3 a = fract(p.xyx*vec3(123.34, 234.34, 345.65));
    a+= dot(a, a+34.45);
    return fract(vec2(a.x*a.y, a.y*a.z));
}
