#define R iResolution
#define k 0.05
#define pi 3.14159265359
#define pi2 6.28318530718


void mainImage( out vec4 O, in vec2 P )
{
    vec2 uv = (P-R.xy/2.0)/min(R.x,R.y);

    float dd = 0.0;
    float cc = 0.375;

    for(float i=0.0;i<2.0;++i) {
        float j = i*2.0-1.0;
        float a = mod(atan((uv.x-cc+2.0*cc*i)*j, uv.y*j)+pi+pi/2.0, pi2);
        float l = length(uv+vec2(-cc+2.0*cc*i,0.0));
        float d = l+cos(a/4.0)*k;
        d = pow(abs(sin(d*pi/k)),13.0);
        l+=cos(a/4.0)*k;
        a+=floor(l/k)*pi2;
        float aa = sqrt(a*a+1.0);
        float d2 = (a*aa+log2(a+aa))*k/(4.0*pi);
        d = d * pow(abs(sin(d2*50.322-iTime*pi*j)), 5.0);
        if(d2>10.0819)d=0.0;
        dd += d;
    }


    vec3 col = vec3(dd,dd,dd);

    // Output to screen
    O = vec4(col,1.0);
}
