// -1 thanks to shadertoyjiang ☁️
// -something thanks to coyote ☁️ 

void mainImage( out vec4 o, vec2 u )
{
    u -= o.xy = iResolution.xy / 2.; 
    float i,
          d = length(u /= o.y) / 4.,
          t = iTime;
    u /= .9 + d;

    for ( o = vec4(0, .3, .6, 0); 
          i++ < 19.;
          u = 1.05 * u * mat2(cos(i*i + vec4(8, 19, 41, 8)))
            + cos(o.yz*4.+t)*.01 + i + cos(1e2*u)/7e2 )          
        o += abs(cos(.2 * (cos(o) + t) + 3. * u.x - .05*t*i))
           -.5;

    o = tanh((.5-.6*d) * o);
}
