renderpass.code:
vec4 T(vec2 U) {
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    U -= .5*A(U).xy;
    return A(U);
}
Main {

    Q = T(U);
    vec4 n = T(U+vec2(0,1));
    vec4 e = T(U+vec2(1,0));
    vec4 s = T(U-vec2(0,1));
    vec4 w = T(U-vec2(1,0));
    Q.xy -= 0.25*vec2(e.z-w.z,n.z-s.z);
    Q.z = 0.25*(e.z+w.z+s.z+n.z-e.x+w.x-n.y+s.y);

    Q.y += .001*Q.w;
    Q.w *= .995;

    vec2 m = .5*R;

    if (iMouse.z>0.) {
        m = iMouse.xy;
        m -= .5*R;
        m *= .5;
        m.y += .1*R.y;
        m += .5*R;
    }

    if (length(U-m) < 10.) Q.z += .01, Q.w +=.01;

    if (iFrame < 1||U.x<1.||R.x-U.x<1.||U.y<1.||R.y-U.y<1.) Q = vec4(.05,0,0,0);

}

