void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord.xy / iResolution.xy;
  vec3 color = vec3(0.5);

  // Psychedelic waves with even higher frequency and amplitude
  uv.x += sin((uv.y + iTime) * 80.0) * 0.8;
  uv.y += cos((uv.x + iTime) * 80.0) * 0.8;
  uv.x += sin((uv.y + iTime) * 160.0) * 0.4;
  uv.y += cos((uv.x + iTime) * 160.0) * 0.4;

  // Swirling pattern with even more intricate calculations
  vec2 center = vec2(0.5);
  float dist = distance(uv, center);
  uv += (center - uv) * (1.0 - dist) * 0.1;
  uv += (center - uv) * (1.0 - dist) * 0.1 * sin(iTime * 20.0);
  uv += (center - uv) * (1.0 - dist) * 0.1 * sin(iTime * 40.0);
  uv += (center - uv) * (1.0 - dist) * 0.1 * sin(iTime * 80.0);

  // Purple color shift with even more oscillations
  color += vec3(0.5, 0.0, 0.5) * sin(uv.x * 80.0 + iTime);
  color += vec3(0.5, 0.0, 0.5) * cos(uv.y * 80.0 + iTime);
  color += vec3(0.25, 0.0, 0.25) * sin(uv.x * 40.0 + iTime * 2.0);
  color += vec3(0.25, 0.0, 0.25) * cos(uv.y * 40.0 + iTime * 2.0);
  color += vec3(0.125, 0.0, 0.125) * sin(uv.x * 20.0 + iTime * 4.0);
  color += vec3(0.125, 0.0, 0.125) * cos(uv.y * 20.0 + iTime * 4.0);
  color += vec3(0.0625, 0.0, 0.0625) * sin(uv.x * 10.0 + iTime * 8.0);
  color += vec3(0.0625, 0.0, 0.0625) * cos(uv.y * 10.0 + iTime * 8.0);

  // Add even more noise for extra visual interest
  color += vec3(0.2) * sin(fragCoord.x * 0.01 + iTime);
  color += vec3(0.2) * cos(fragCoord.y * 0.01 + iTime);
  color += vec3(0.1) * sin(fragCoord.x * 0.005 + iTime * 2.0);
  color += vec3(0.1) * cos(fragCoord.y * 0.005 + iTime * 2.0);
  color += vec3(0.05) * sin(fragCoord.x * 0.0025 + iTime * 4.0);
  color += vec3(0.05) * cos(fragCoord.y * 0.0025 + iTime * 4.0);

  fragColor = vec4(color, 1.0);
}
