void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float sinTime = (sin(iTime) + 1.0) * 0.5;

    float grad = uv.x  - pow(uv.y, sinTime);
    float curveStep = 1.0 - step(grad, 0.01);


    fragColor = vec4(curveStep, curveStep, curveStep, 1.0);
}
