#define NUM_EXPLOSIONS 5.
#define NUM_PARTICLES 50.

float random (float x) {
    return fract(45989.3145847*sin(x*51.9735894))*2. - 1.; }

vec2 random_polarcords (float t) {
    float a = fract (sin(t * 993.7) * 322.2)*2.0*3.14159265;
    float b = fract ((sin(t*a) * 993.7) * 322.2);
    return vec2(sin(a), cos(a)) * b; }

float Explosion(vec2 uv, float t) {
    float sparks = 0.001;
    for (float i = 0.0; i<NUM_PARTICLES; i++) {
        vec2 dir = random_polarcords(i+1.0)*0.9;   
        float d = (length(uv-dir*smoothstep(0., 1.0, t*t)));
        float light = min(0.7, mix(0.0005, 0.0005, smoothstep(0.05, 0.0, t)));
        light *= sin(t*20.0+i)*0.5 + 0.5;
        light *= smoothstep(1.0, 0.8, t);
        sparks += light/d; } return sparks; }

vec2 Bezier(vec2 a, vec2 b, vec2 c, float t) {
    return mix(mix(a, c, t), mix(c, b, t), t); }

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    uv.y += 0.5;
    vec3 col = vec3(0.0);
    float t = iTime;
    float rt = floor(t*0.5);
    for (float i = 0.; i < NUM_EXPLOSIONS; i++) {
        float t = iTime+(i/NUM_EXPLOSIONS)*171.532;
        float rt = floor(t*0.5);
        t = mod(t, 2.);
        vec2 A = vec2(random(rt)*0.7, 0.);
        vec2 B = vec2(clamp(A.x*1.1, -0.5, 0.5), 0.4);
        vec2 C = vec2(clamp(B.x*1.3, -0.5, 0.5), 0.6);
        vec3 color = sin(vec3(4.59, 6.2, 2.8)+rt)*0.25+0.45;
        col += Explosion(uv - Bezier(A, C, B, t), smoothstep(1., 2., t))*mix(0.03, 1.0, smoothstep(1.1, 1.5, t))*color*smoothstep(0., 0.5, t); }
    col *= 3.0;
    fragColor = vec4(col,1.0); }
