void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // middle of screen
    vec2 uv = (fragCoord.xy / iResolution.xy - 0.5) * 2.0;

    // normalize UV
    uv.x *= iResolution.x/iResolution.y;

    // radius of the circle
    float radius = 0.1;

    // calculate the distance
    float distance = length(uv) - radius;

    // anti alias the circle
    float circ = smoothstep(0.495, 0.5, distance);


    // define the color for the circle
    vec4 color = vec4(1.0, 1.0, 1.0, 1.0); 

    // interpolate the circle color, with the background color and the circle 
    fragColor = mix(color, vec4(0.0, 0.0, 0.0, 0.0), circ);
}
