float boxer(vec2 uv, vec2 pos)
{    
    float box = max(abs(uv.x + pos.x),abs(uv.y + pos.y))-.1;
    return smoothstep(0.009,0.01,box);
}

float circle(vec2 uv, vec2 pos, float radius)
{    
    float circ = length(uv + pos);
    return smoothstep(0.009,0.01,circ - radius);
}

vec2 transform(vec2 shear, vec2 scale, vec2 uv)
{
    vec2 st = uv;
    st *= mat2(
        1, -sin(shear.x),
       -sin(shear.y), 1);

    st -= 0.5;

    st *= vec2(scale.x,scale.y);

    return st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 shear1 = vec2(0.3,0.0);
    vec2 scale1 = vec2(2.,0.4);
    vec2 uv1 = transform(shear1,scale1,uv);

    vec2 shear2 = vec2(-0.3,0.0);
    vec2 scale2 = vec2(2.,0.55);
    vec2 uv2 = transform(shear2,scale2,uv);

    vec2 shear3 = vec2(0.0);
    vec2 scale3 = vec2(1.0);
    vec2 uv3 = transform(shear3,scale3,uv);

    vec2 shear4 = vec2(-0.3,0.0);
    vec2 scale4 = vec2(2.,0.4);
    vec2 uv4 = transform(shear4,scale4,uv);


    vec2 pos = vec2(0.5,0.00);
    vec2 pos2 = vec2(-0.24,0.04);
    vec2 pos3 = vec2(-0.0,-0.25);
    vec2 pos4 = vec2(-0.5,0.00);

    float box = boxer(uv1,pos);
    float box2 = boxer(uv2,pos2);
    float circ = circle(uv3,pos3,.07);
    float box3 = boxer(uv4,pos4);

    float finals = box * box2 * circ * box3;

    finals = smoothstep(0.09,0.001,finals);

    vec3 image = mix(vec3(.8,0.2,0.3),vec3(1.),finals);
    // Output to screen
    fragColor = vec4(image,0.0);
}
