void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=4.,e,t=iTime; vec3 p,q=vec3(3,6,-2),r=iResolution;
o=vec4(0.);

for(i=0.;i++<9.&&d>1e-4;){
    p=vec3((FC.xy-.5*r.xy)/r.y,e);
    p.yz*=rotate2D(t*.5);
    p+=mod(length(p),1.)-.5;

    for(;j++<16.;)
        p=abs(p)*rotate3D(atan(i),q)-.04,
        d=max(max(p.y,p.z), p.x)/d,
        d-=dot(p,q)/1e4;

    e-=d;
}o+=vec4(sqrt(p/i*j),1)*3./i;
}
