void mainImage(out vec4 o, vec2 u){
    vec2 r = iResolution.xy;
    u = 10. * (u - r / 2.) / r.y;

    float T = iTime;

    T = floor(T) + smoothstep(.0, .5, fract(T));

    u.x -= mod(T, 18.) - 9.;

    o = vec4(
            smoothstep(24./r.y, .0, 
                length(u) - .5
            )
        );
}





