float cDist(vec2 p1, vec2 p2, float r)
{
    return length(p1 - p2) - r;
}

float lDist(vec2 p, float h)
{
    return p.y - h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    uv *= 5.0;

    vec3 col1 = vec3(0.0, 1.0, 1.0);
    vec3 col2 = vec3(0.0, abs(sin(6.0 * iTime + uv.x)), 0.6);

    float d1 = cDist(uv, vec2(0.0, 3.0 * abs(sin(6.0 * iTime)) - 1.0), 1.0);
    d1 = smoothstep(0.0, 2.0, abs(d1) + 0.4);
    d1 = 0.1 / d1;
    col1 *= d1;

    float d2 = lDist(uv, -2.0);
    d2 = smoothstep(0.0, 2.0, abs(d2) + 0.4);
    d2 = 0.04 / d2;
    col2 *= d2;

    vec3 col = col1 + col2;

    fragColor = vec4(col,1.0);
}

#define pi 3.14159265358979323846

vec2 mainSound( int samp, float time )
{
    float time1 = mod(time, pi / 6.0);
    return 0.3 * vec2(sin(6.2831 * 440.0 * time1) * exp(-3.0 * time1));
}
