vec2 centre;
float r;
vec2 centreAround;
float rAround;
vec2 centreAround2;
float rAround2;
vec2 centreAround3;
float rAround3;
void setCircle(){
    r = 0.15;
    centre = vec2(cos(iTime)*1.12, -sin(iTime)*0.74 - 0.5);
}
void setCircleAround(){
    rAround = 0.18;
    centreAround = vec2(cos(iTime)*1.12, -sin(iTime)*0.74 - 0.5);
    //centreAround = vec2(sin(iTime)*1.12,cos(iTime)*0.74 - 0.5);
}
void setCircleAround2(){
    rAround2 = 0.20;
    centreAround2 = vec2(cos(iTime)*1.12, -sin(iTime)*0.74 - 0.5);
}
void setCircleAround3(){
    rAround3 = 0.21;
    centreAround3 = vec2(cos(iTime)*1.12, -sin(iTime)*0.74 - 0.5);
    //centreAround3 = vec2(0.0, 0.0);
}
void setBG(out vec3 BGColor){
    BGColor = vec3(0.0 + 0.4*(-sin(iTime)) + 0.5*(cos(iTime*0.8)), 0.0 + 0.8*(-sin(iTime)) - 0.9*abs(cos(iTime*0.5)), 0.0 + 0.82*(-sin(iTime)));
    //BGColor = vec3(0.4, 0.4 - sin(iTime)*0.4, 0.9);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 BGColor;
    setBG(BGColor);
    setCircle();
    setCircleAround();
    setCircleAround2();
    setCircleAround3();
    fragColor = vec4(BGColor, 1.0);
    vec2 tmp = fragCoord/iResolution.xy;
    tmp -= 0.5; //made the coordinate center in the middle
    tmp.x *= iResolution.x/iResolution.y; //alignment



    vec4 m = iMouse / iResolution.x;
    if (m.z>0.0){
        fragColor = vec4(0.02 + 0.1*(sin(iTime)), 0.0, 0.34 + 0.4*(sin(iTime)),0.0);
        if ((tmp.x - centre.x)*(tmp.x - centre.x) + (tmp.y - centre.y)*(tmp.y - centre.y) <= r*r){
            fragColor = vec4(-sin(iTime) - 0.1, -sin(iTime) - 0.1, -sin(iTime) - 0.1, 0.0);
        }
        else if ((tmp.x - centreAround.x)*(tmp.x - centreAround.x) + (tmp.y - centreAround.y)*(tmp.y - centreAround.y) <= rAround*rAround){
            fragColor = vec4(-sin(iTime) - 0.15, -sin(iTime) - 0.15, -sin(iTime) - 0.15, 0.0);
        }
        else if ((tmp.x - centreAround2.x)*(tmp.x - centreAround2.x) + (tmp.y - centreAround2.y)*(tmp.y - centreAround2.y) <= rAround2*rAround2){
            fragColor = vec4(-sin(iTime) - 0.20, -sin(iTime) - 0.20, -sin(iTime) - 0.20, 0.0);
        }
        else if ((tmp.x - centreAround3.x)*(tmp.x - centreAround3.x) + (tmp.y - centreAround3.y)*(tmp.y - centreAround3.y) <= rAround3*rAround3){
            fragColor = vec4(-sin(iTime) - 0.25, -sin(iTime) - 0.25, -sin(iTime) - 0.25, 0.0);
        }
    }
    else{
        if ((tmp.x - centre.x)*(tmp.x - centre.x) + (tmp.y - centre.y)*(tmp.y - centre.y) <= r*r){
            fragColor = vec4(0.94, 0.5 - sin(iTime)*0.3, 0.05 , 0.0);
        }
        else if ((tmp.x - centreAround.x)*(tmp.x - centreAround.x) + (tmp.y - centreAround.y)*(tmp.y - centreAround.y) <= rAround*rAround){
            fragColor = vec4(0.94, 0.6 - sin(iTime)*0.3, 0.03 , 0.0);
        }
        else if ((tmp.x - centreAround2.x)*(tmp.x - centreAround2.x) + (tmp.y - centreAround2.y)*(tmp.y - centreAround2.y) <= rAround2*rAround2){
            fragColor = vec4(0.94, 0.67 - sin(iTime)*0.3, 0.2 , 0.0);
        }
        else if ((tmp.x - centreAround3.x)*(tmp.x - centreAround3.x) + (tmp.y - centreAround3.y)*(tmp.y - centreAround3.y) <= rAround3*rAround3){
            fragColor = vec4(0.94, 0.75 - sin(iTime)*0.3, 0.2 , 0.0);
        }
    }
}
