vec3 mapFloatToColor(float value) {
   vec3 col = 0.5 + 0.2*cos((value+vec3(0,2,4)));
   return col;
}

// calc the position of light point
vec2 circularPoint( vec2 pos, float R, float time,float x)
{
    float ratio = iResolution.x/iResolution.y;
    return pos + vec2(sin(time*1.2*x -6.0)*R,cos(time*1.4*x -6.0)*R*ratio);
}

float flashing(float time)
{
    return 0.6 + 0.2 * sin(2.0*time) * sin(time);
}


float lighting(vec2 fragCoord,vec2 position)
{
    return 1.0/abs(sqrt(distance(fragCoord,position)*0.5));
}



vec3 renderPointsAtTime(vec2 fragCoord,float currentTime){

    float scale = iResolution.x / 800.0;
    float R = 32.0/800.0;
    vec3 color = vec3(0,0,0);
    vec2 position = vec2(0.5,0.5);
    if(iMouse.x>0.0&&iMouse.y>0.0){
     position = iMouse.xy/iResolution.xy;
    }

    for(float i = 1.0;i<6.0;i++){
     float time = currentTime * (float(i)/2.0);
     float lightingEffect = lighting(fragCoord,circularPoint(position,R,time,i)) * flashing(time);
     vec3 newcolor =lightingEffect *  mix(mapFloatToColor(currentTime*(i+1.0)/2.0), vec3(0.02, 0.01, 0.02), 0.95);
     color = max(color,newcolor);
    }
    return color;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
     vec2 uv = fragCoord/iResolution.xy;


    vec3 color = vec3(0,0,0);

    for(float i = 0.0;i<15.0;i++){
        float currentTime = iTime - i * 0.02;
        vec3 newcolor = renderPointsAtTime(uv,currentTime)*((15.0-i)/15.0);
        color = max(color,newcolor);
    }

    // Output to screen
    fragColor = vec4(color, 1.0);
}
