float line(vec2 uv, float a, float b, float c) {
    float distance_ = abs(a*uv.x + b*uv.y + c) / sqrt(a*a + b*b);
    float glow = pow(1. - distance_, 40.);
    // not sure why nearly zero instead of just 0 ...
    float mask = clamp(glow, .00000001, 1.);
    return mask;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= -iResolution.x / iResolution.y;

    float mask = 0.;
    mask += line(uv, 1., 2., 0.);
    mask += line(uv, -1., 2., -1.);
    mask += line(uv, -1., -1., -1.);
    mask += line(uv, 1., 0., 1.);
    mask += line(uv, 0., 1., -.5);

    vec3 col = vec3(mix(vec3(0.07), vec3(.84,.89,.28), mask));
    fragColor = vec4(col, 1.);
}
