void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
         U = 2.* abs( 2.*u - R ) / R.y - vec2(.5,0), // quadrant symmetry
         I = floor(U),                               // big cells id
         F = abs(U-I-.5)*2. -.5;                     // quadrants in cells 
         F = ( F + vec2(-F.y,F) )*.7;                // rot 45°
    F.x = abs(F.x);                                  // local mirror symmetry
    O = vec4( I.y == 1. || I.x == 2.                 // crown only
                ? smoothstep(0., 12./R.y,            // AA draw
                             min( abs( abs(length(F-vec2(.3,0))-.15) -.08), // double circle
                             abs(F.y+.23) )                                 // line
                            )
                : 1.
            );        
 }
